using System;using System.Web.Mvc;using Microsoft.ApplicationInsights;namespace Skeleton.Web.Telemetry{    [AttributeUsage(AttributeTargets.Class | AttributeTargets.Method, AllowMultiple = true)]    public class AiHandleErrorAttribute : HandleErrorAttribute    {        public override void OnException(ExceptionContext filterContext)        {            if ((filterContext != null) && (filterContext.HttpContext != null) && (filterContext.Exception != null))                if (filterContext.HttpContext.IsCustomErrorEnabled)                {                    var ai = new TelemetryClient();                    ai.TrackException(filterContext.Exception);                }            base.OnException(filterContext);        }    }}